CREATE TABLE `products` (
  `id` int NOT NULL,
  `name` varchar(255) NOT NULL,
  `description` text,
  `price` decimal(10,2) NOT NULL,
  `image` varchar(255) NOT NULL,
  `technical_specs` text
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

--
-- Дамп данных таблицы `products`
--

INSERT INTO `products` (`id`, `name`, `description`, `price`, `image`, `technical_specs`) VALUES
(1, 'Токарный станок', 'Токарный станок предназначен для точной обработки металлических заготовок, таких как цилиндры, конусы и другие формы вращения. Используется для нарезки резьбы, создания фасок, расточки отверстий и шлифовки поверхностей. Идеально подходит для мелкосерийного производства и индивидуального изготовления деталей. Этот станок отличается надежностью, легкостью в управлении и высокой точностью.', '15000.00', 'lathe.png', 'Мощность: 5 кВт, Скорость вращения шпинделя: 100-3000 об/мин, Диаметр обработки: до 400 мм, Длина обработки: до 1000 мм.'),
(2, 'Фрезерный станок', 'Фрезерный станок с числовым программным управлением (ЧПУ) представляет собой высокотехнологичное оборудование, используемое для обработки сложных поверхностей. Это идеальный выбор для производства пресс-форм, зубчатых передач, корпусов и других деталей с высокой точностью. Удобное программное управление позволяет создавать сложные элементы и сокращать время обработки.', '22000.00', 'milling_machine.png', 'Мощность: 7.5 кВт, Скорость шпинделя: 50-4000 об/мин, Рабочая зона: 800x600 мм, Точность: ±0.01 мм.'),
(3, 'Сверлильный станок', 'Станок для сверления отверстий в металле и других твердых материалах. Подходит для создания отверстий различных диаметров, нарезки резьбы и обработки заготовок сложной формы. Используется на производственных предприятиях, в мастерских и учебных заведениях. Станок характеризуется простотой управления и долговечностью.', '10000.00', 'drilling_machine.png', 'Мощность: 1.5 кВт, Диаметр сверления: до 50 мм, Глубина сверления: до 150 мм, Вес: 120 кг.'),
(4, 'Шлифовальный станок', 'Шлифовальный станок предназначен для обработки и полировки поверхностей, удаления заусенцев, улучшения качества поверхности и подготовки заготовок к дальнейшей обработке. Подходит для металлов, дерева и композитных материалов. Имеет возможность точной настройки глубины шлифовки, что обеспечивает идеальный результат.', '18000.00', 'grinding_machine.png', 'Мощность: 3 кВт, Размер шлифовального круга: 200 мм, Скорость вращения: 1400 об/мин, Вес: 200 кг.'),
(5, 'Плазменный резак', 'Плазменный резак — это высокоэффективное оборудование для резки металлов, включая нержавеющую сталь, алюминий и медные сплавы. Использует поток плазмы для точной и быстрой обработки, что минимизирует деформацию заготовок. Подходит для строительства, производства и ремонтных работ.', '25000.00', 'plasma_cutter.jpg', 'Мощность: 10 кВт, Толщина реза: до 20 мм, Рабочее напряжение: 220/380 В, Тип газа: компрессированный воздух.'),
(6, 'Лазерный резак', 'Лазерный резак — это оборудование для высокоточной резки различных материалов, включая металлы, пластик, дерево и акрил. Идеально подходит для создания сложных узоров, декоративных элементов и промышленных заготовок. Обеспечивает высокую скорость работы, отсутствие механического износа и точность до микрона.', '35000.00', 'laser_cutter.png', 'Мощность лазера: 1500 Вт, Рабочая зона: 1000x1000 мм, Точность: ±0.05 мм, Тип лазера: CO2.'),
(7, 'Станок для гибки металла', 'Станок для гибки металла используется для формирования сложных изгибов и геометрий на металлических листах. Применяется в производстве строительных конструкций, мебельной фурнитуры, автомобильных деталей и бытовой техники. Станок отличается надежностью, компактностью и высокой производительностью.', '12000.00', 'metal_bender.jpg', 'Мощность: 2 кВт, Толщина гибки: до 10 мм, Угол гибки: до 120°, Вес: 300 кг.'),
(8, 'Прокатный станок', 'Прокатный станок предназначен для обработки металла путем прокатки, что позволяет получать заготовки с равномерной толщиной и улучшенными механическими свойствами. Широко применяется в металлургической промышленности, для создания стальных листов, профилей и труб. Обеспечивает стабильное качество продукции и экономичность процесса.', '30000.00', 'rolling_machine.png', 'Мощность: 15 кВт, Ширина прокатки: до 600 мм, Толщина листа: до 20 мм, Вес: 1500 кг.'),
(9, 'Штамповочный пресс', 'Штамповочный пресс — это оборудование для производства металлических деталей методом штамповки. Используется для изготовления корпусных элементов, крышек, деталей машин и других изделий. Пресс обеспечивает высокую скорость работы, точность и возможность производства сложных форм.', '45000.00', 'press_machine.jpg', 'Мощность: 20 кВт, Усилие: до 500 тонн, Размер стола: 1000x800 мм, Вес: 3000 кг.'),
(10, 'Токарный станок с ЧПУ', 'Токарный станок с числовым программным управлением предназначен для автоматической обработки сложных металлических заготовок. Используется для нарезки резьбы, создания сложных контуров и точной обработки поверхностей. Подходит для массового производства и позволяет минимизировать влияние человеческого фактора.', '40000.00', 'cnc_lathe.png', 'Мощность: 10 кВт, Скорость вращения шпинделя: 100-5000 об/мин, Длина обработки: до 1500 мм, Система управления: Fanuc.'),
(11, 'Фрезерный станок с ЧПУ', 'Фрезерный станок с ЧПУ представляет собой оборудование нового поколения, обеспечивающее максимальную точность и автоматизацию процессов обработки металлов. Применяется для производства высокоточных деталей, инструментов и форм. Поддерживает интеграцию с CAD/CAM системами для ускорения и оптимизации работы.', '50000.00', 'cnc_milling_machine.png', 'Мощность: 15 кВт, Рабочая зона: 1200x800 мм, Точность: ±0.01 мм, Поддержка CAD/CAM.'),
(12, 'Станок для нарезки резьбы', 'Станок для нарезки резьбы предназначен для создания наружной и внутренней резьбы на заготовках различного диаметра. Подходит для производства соединительных элементов, таких как болты, гайки и фитинги. Обеспечивает стабильное качество резьбы и высокую производительность.', '14000.00', 'threading_machine.jpg', 'Мощность: 2.2 кВт, Диаметр резьбы: M6-M50, Скорость вращения: 200-2000 об/мин, Вес: 300 кг.'),
(13, 'Автоматический пилорама', 'Автоматический пилорама предназначен для высокоточной распиловки древесины. Используется в деревообрабатывающей промышленности, строительстве и производстве мебели. Оборудование оснащено системами автоматической подачи и точной настройки параметров резки.', '35000.00', 'sawing_machine.jpg', 'Мощность: 7 кВт, Максимальный диаметр бревна: до 700 мм, Длина реза: до 6000 мм, Автоматическая подача.'),
(14, 'Станок для сварки', 'Станок для сварки подходит для выполнения сварочных работ с различными материалами, включая сталь, алюминий и нержавеющие сплавы. Поддерживает несколько режимов работы, включая ручной, полуавтоматический и автоматический. Обеспечивает надежные соединения и высокую производительность.', '22000.00', 'welding_machine.jpg', 'Мощность: 5 кВт, Тип сварки: MIG/TIG, Толщина металла: до 10 мм, Рабочее напряжение: 220 В.'),
(15, 'Станок для нанесения покрытия', 'Станок для нанесения покрытия используется для защиты металлических изделий от коррозии и придания им эстетичного внешнего вида. Применяется в автомобильной, строительной и машиностроительной отраслях. Поддерживает широкий выбор покрытий, включая порошковые и лакокрасочные.', '28000.00', 'coating_machine.jpg', 'Мощность: 4 кВт, Тип покрытия: порошковое, Емкость резервуара: 20 л, Поддержка автоматизации.'),
(16, 'Станок для шовной сварки', 'Станок для шовной сварки предназначен для соединения металлических деталей по длинным швам. Используется в производстве труб, резервуаров, корпусов и других конструкций. Обеспечивает равномерное, герметичное и прочное соединение.', '25000.00', 'seam_welding_machine.jpg', 'Мощность: 8 кВт, Толщина металла: до 12 мм, Скорость сварки: до 1 м/мин, Рабочее напряжение: 380 В.');

